    public static int findMaxConsecutiveOnes(int[] nums){
        int count = 0;
        int lastCount = 0;
        for(int i = 0; i < nums.length; i++){
            if (nums[i] == 1) {
                count++;
                if(lastCount <= count) {
                    lastCount = count;
                }
            } else {
                count = 0;
            }
        }
        return lastCount;
    }

    public static int findMaxConsecutiveOnes2(int[] nums) {
        int halfway = (nums.length / 2);
        int i = halfway;
        int j = halfway+1;
        int countI = 0;
        int lastICount = 0;
        int countJ = 0;
        int lastJCount = 0;
        int innerCountI = 0;
        int innerCountJ = 0;
        boolean innerCountIPass = true;
        boolean innerCountJPass = true;
        while(i >= 0 || j < nums.length){
            if(i >= 0){
                if (nums[i] == 1) {
                    countI++;
                    if(lastICount <= countI) {
                        lastICount = countI;
                    }
                } else {
                    if(innerCountIPass){
                        innerCountIPass = false;
                        innerCountI = countI;
                    }
                    countI = 0;
                }
            }

            if(j < nums.length){
                if (nums[j] == 1) {
                    countJ++;
                    if(lastJCount <= countJ) {
                        lastJCount = countJ;
                    }
                } else {
                    if(innerCountJPass){
                        innerCountJPass = false;
                        innerCountJ = countJ;
                    }
                    countJ = 0;
                }
            }
            i--;
            j++;
        }

        int individualCount = lastICount > lastJCount ? lastICount : lastJCount;
        int innerCountTotal = 0;
        if(!innerCountJPass && !innerCountIPass){
            innerCountTotal = innerCountJ + innerCountI;
        } else if(!innerCountJPass && innerCountIPass){
            innerCountTotal = innerCountJ + lastICount;
        } else if(innerCountJPass && !innerCountIPass){
            innerCountTotal = lastJCount + innerCountI;
        } else {
            innerCountTotal = lastJCount + lastICount;
        }
        return innerCountTotal > individualCount ? innerCountTotal : individualCount;
    }
